testcase.input:
public class A {
    public boolean foo() {
        if (B.b) {
            return true;
        } else { 
            return false;
        }
    }
    static class B {
        static boolean b;
    }
}

testcase.result:
public class A {
    public boolean foo() {
        return B.b;
    }
    static class B {
        static boolean b;
    }
}

testcase.input:
public class A {
    public boolean foo() {
        if (B.b) {
            return false;
        } else { 
            return true;
        }
    }
    static class B {
        static boolean b;
    }
}

testcase.result:
public class A {
    public boolean foo() {
        return !B.b;
    }
    static class B {
        static boolean b;
    }
}
