testcase.input:
public class A {
    boolean a, b;
    public boolean foo() {
        if ((a | b)) {
            return true;
        } else { 
            return false;
        }
    }
}

testcase.result:
public class A {
    boolean a, b;
    public boolean foo() {
        return (a | b);
    }
}

testcase.input:
public class A {
