testcase.input:
public class A extends B {
    public boolean foo() {
        if (super.b) {
            return true;
        } else { 
            return false;
        }
    }
}

testcase.result:
public class A extends B {
    public boolean foo() {
        return super.b;
    }
}

testcase.input:
public class A extends B {
    public boolean foo() {
        if (super.b) {
            return false;
        } else {
            return true;
        }
    }
}

testcase.result:
public class A extends B {
    public boolean foo() {
