testcase.input:
public class A extends B {
    public boolean foo() {
        if (super.bar()) {
            return true;
        } else { 
            return false;
        }
    }
}

testcase.result:
public class A extends B {
    public boolean foo() {
        return super.bar();
    }
}

testcase.input:
public class A extends B {
    public boolean foo() {
        if (super.bar()) {
            return false;
        } else {
            return true;
        }
    }
}

testcase.result:
public class A extends B {
    public boolean foo() {
        return !super.bar();
    }
}
