testcase.input:
public class Boolean {
    public boolean foo() {
        if (Boolean.this) {
            return true;
        } else { 
            return false;
        }
    }
}

testcase.result:
public class Boolean {
    public boolean foo() {
        return Boolean.this;
    }
}

testcase.input:
public class Boolean {
    public boolean foo() {
        if (Boolean.this) {
            return false;
        } else { 
            return true;
        }
    }
}

testcase.result:
public class Boolean {
    public boolean foo() {
        return !Boolean.this;
    }
}
